/** tạo lỗi */

module.exports = server =>
    (server.setError = function({ code, http_code, message } = {}) {
        let error = server.options.errors.find(x => x.code === code);

        if (error) {
            error.http_code = http_code;
            error.message = message;
        } else {
            server.options.errors.push({
                code,
                http_code,
                message
            });
        }
    });
