/** tạo bảng */

module.exports = ({ elastic }) =>
    (elastic.indices.createTable = async function({ table_name, mappings }) {
        return await elastic.client.indices.create({
            index: elastic.options.prefix + table_name,
            body: {
                mappings: {
                    [table_name]: {
                        properties: mappings
                    }
                }
            }
        });
    });
