/** đặt filter must */

module.exports = ({ elastic, query }) =>
    (query.filterMust = function(queryData) {
        if (!query._body.filter_must) query._body.filter_must = [];

        if (Array.isArray(queryData)) {
            query._body.filter_must = query._body.filter_must.concat(queryData);
        } else query._body.filter_must.push(queryData);

        return query;
    });
