/** đặt filter must not */

module.exports = ({ elastic, query }) =>
    (query.filterMustNot = function(queryData) {
        if (!query._body.filter_must_not) query._body.filter_must_not = [];

        if (Array.isArray(queryData)) {
            query._body.filter_must_not = query._body.filter_must_not.concat(
                queryData
            );
        } else query._body.filter_must_not.push(queryData);

        return query;
    });
