/** đặt filter should */

module.exports = ({ elastic, query }) =>
    (query.filterShould = function(queryData) {
        if (!query._body.filter_should) query._body.filter_should = [];

        if (Array.isArray(queryData)) {
            query._body.filter_should = query._body.filter_should.concat(
                queryData
            );
        } else query._body.filter_should.push(queryData);

        return query;
    });
