/** đặt sort */

module.exports = ({ elastic, query }) =>
    (query.sort = function(sortData) {
        if (!query._body.sort) query._body.sort = [];

        if (Array.isArray(sortData)) {
            query._body.sort = query._body.sort.concat(sortData);
        } else query._body.sort.push(sortData);

        return query;
    });
