/** khởi tạo lỗi http
 * @param {Error} error lỗi mặc định
 * @param {string} code mã định danh lỗi
 * @param {string} message câu thông báo lỗi
 * @param {object} data dữ liệu mở rộng
 * @param {number} http_code http status code
 */

module.exports = expo =>
    (expo.HttpError = class HttpError extends Error {
        constructor({ error, code, message, data, http_code } = {}) {
            super(message ? message : error.message);
            this.name = this.constructor.name;
            Error.captureStackTrace(this, this.constructor);

            this.code = code;
            this.http_code = http_code;
            this.data = data;
        }
    });
