/** kiá»ƒu timestamp */

let Moment = require('../../moment/_index');

module.exports = joi =>
    joi.extend([
        {
            name: 'timestamp',
            base: joi
                .date()
                .timestamp('javascript')
                .meta({ baseType: 'number' }),
            pre(value, state, options) {
                if (options.convert) return Moment(value).valueOf();
                return value;
            }
        }
    ]);
