/** khởi tạo model client
 * @param {object} options thông số cấu hình
 * @returns {Model} model
 */

module.exports = expo =>
    (expo.init = function() {
        let client = {
            models: [],
            events: {}
        };

        // cài đặt các method cho đối tượng model
        require('./instance/_index')(client);

        return client;
    });
