/** phát sinh chuỗi ngẫu nhiên
 * @param {number} length độ dài chuỗi
 * @param {string} characters chuỗi các ký tự chấp nhận phát sinh
 * @returns {string} chuỗi kết quả
 */

module.exports = expo =>
    (expo.randomString = function({
        length,
        characters = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789'
    } = {}) {
        let result = '';

        let l = characters.length;
        for (let i = 0; i < length; i++) {
            result += characters.charAt(Math.floor(Math.random() * l));
        }

        return result;
    });
