/** phát sinh chuỗi uuid ngẫu nhiên
 * @param {boolean} no_dash bỏ các ký tự '-'?
 * @returns {string} chuỗi kết quả
 */

let uuid = require('uuid/v4');

module.exports = expo =>
    (expo.randomUuid = function({ no_dash = false } = {}) {
        let result = uuid();

        if (no_dash) result = result.replace(/-/g, '');

        return result;
    });
