/** giả mã hex chuỗi uuid
 * @param {buffer} buffer chuỗi uuid đã mã hóa
 * @returns {string} chuỗi uuid đã giải mã
 */

module.exports = expo =>
    (expo.unhexUuid = function(buffer) {
        let uuid = buffer.toString('hex');
        return (
            uuid.substring(0, 8) +
            '-' +
            uuid.substring(8, 12) +
            '-' +
            uuid.substring(12, 16) +
            '-' +
            uuid.substring(16, 20) +
            '-' +
            uuid.substring(20)
        );
    });
