module.exports = class Logger {
    constructor({ canLog }) {
        this.canLog = canLog;
    }

    log(...arg) {
        if (this.canLog === true) {
            console.log(...arg);
        }
    }

    trace(...arg) {
        if (this.canLog === true) {
            console.trace(...arg);
        }
    }

    error(...arg) {
        if (this.canLog === true) {
            console.error(...arg);
        }
    }
};