'use strict';

const _extendBody = function ({ params }) {
  let result = '';
  const types = [
    {
      header: 'content-type',
      value: 'application/json',
      prefix: '-d',
    },
    {
      header: 'enctype',
      value: 'application/x-www-form-urlencoded',
      prefix: '-F',
    },
    {
      header: 'enctype',
      value: 'multipart/form-data',
      prefix: '-F',
    }
  ];

  for (let data of types) {
    if (params.headers && params.headers[data.header] && params.headers[data.header].includes(data.value)) {
      if (data.prefix === '-F') {
        for (let key in params.headers) {
          result += `${data.prefix} ${key}=${params.headers[key]} `;
        }
        return result;
      }
    }
  }
  try {
    result = `-d '${JSON.stringify(params.body)}' `;
  } catch (e) {
    result = `-d '${params.body}'`;
  } finally {
    return result;
  }
}

const _buildCurl = function (params) {
  if (!params.url) {
    console.error('Input missing: URL');
  }

  if (!params.verb) {
    console.error('Input missing: HTTP verb');
  }

  let _headers = '';
  let _body = '';

  try {
    if (params.headers) {
      if (params.headers['content-length']) {
        delete params.headers['content-length'];
      }

      for (let key in params.headers) {
        _headers += `-H '${key}:${params.headers[key]}' `;
      }

      if (params.body) {
        _body += _extendBody({ params });
        // if (params.headers && params.headers['content-type'] && params.headers['content-type'].includes('application/json')) {
        //   _body += `-d '${JSON.stringify(params.body)}' `;
        // } else {
        //   return '[error]: only support content-type application/json';
        // }
      }

      _headers.length ? _headers = _headers.substring(0, _headers.length - 1) : null;
      _body.length ? _body = _body.substring(0, _body.length - 1) : null;

      var curl = `curl ${_headers} -X ${params.verb.toUpperCase()} '${params.url}' ${_body}`
      return curl;
    }
  } catch (e) {
    
  }
};

const printCurl = function (req) {
  var curlParams = {};
  curlParams.url = req.protocol + '://' + (req.headers.host || req.hostname) + req.originalUrl;
  curlParams.verb = req.method.toUpperCase();
  req.headers ? curlParams.headers = req.headers : null;
  req.body ? curlParams.body = req.body : null;
  const curl = _buildCurl(curlParams);
  return curl;
};

module.exports = printCurl;