(async () => {
    const RequestOut = require('./index').RequestOut;

    await RequestOut.setup({
        enable: true,
        service_name: 'test2',
        connection: {
            host: 'https://elk.lixiapp.com/',
            keepAlive: false,
            log: 'trace',
        },
        filter: (data) => {
            return true;
        },
        customResponse: (data) => {
            if (data.method.toLowerCase() === 'get' && data.http_code === 200) {
                delete data.response_data;
                delete data.response_header;
            }
            return data;
        },
    });

    const res = await require('axios')({
        method: 'GET',
        url: 'https://deliapi.lixiapp.com/api/user/payment/method_list?store_id=12312312312',
    });
    console.log(res);
})()