let { Config } = require('../../index');

test(`load config`, async () => {
    let config = Config.loadFromEnv({
        node_mode: 'config',
        dirname: './test/config'
    });

    expect(config.NODE_ENV).toEqual('config');
    expect(config.boolean).toEqual(true);
    expect(config.number).toEqual(1234567);
    expect(config.string).toEqual('hello');
    expect(config.null).toEqual(null);
    expect(config.undefined).toEqual(undefined);
});
