let { Joi, Elasticsearch } = require('../../index');

test(`count`, async () => {
    let es = Elasticsearch.init({ node: 'http://10.39.93.27:9202' });

    let result = await es
        .query()
        .index('lixi_core_unit_test')
        .count();

    let schema = Joi.number();

    Joi.assert(result, schema);
});
