let { Elasticsearch } = require('../../index');

test(`exists`, async () => {
    let es = Elasticsearch.init({ node: 'http://10.39.93.27:9202' });

    let result = await es
        .query()
        .index('lixi_core_unit_test')
        .id('random_id')
        .exists();

    expect(result).toEqual(false);
});
