let {
    Err: { HttpError }
} = require('../../index');

test(`create error`, async () => {
    let code = 'test-code';
    let message = 'Test message';
    let data = { id: 1, name: 'TripleSix' };
    let http_code = 400;

    let result = new HttpError({ code, message, data: data, http_code });

    expect(result.name).toEqual('HttpError');
    expect(result.code).toEqual(code);
    expect(result.message).toEqual(message);
    expect(result.data).toEqual(data);
    expect(result.http_code).toEqual(http_code);
});
