let { Joi, Util } = require('../../index');

test(`random with length 100`, async () => {
    let length = 100;

    let schema = Joi.string().length(length);

    let result = Util.randomString({ length });

    Joi.assert(result, schema);
});

test(`random with length 100 and specified characters`, async () => {
    let length = 100;
    let characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';

    let schema = Joi.string()
        .regex(new RegExp(`[${characters}]`))
        .length(length);

    let result = Util.randomString({ length, characters });

    Joi.assert(result, schema);
});
