let { Joi, Util } = require('../../index');

test(`random uuid`, async () => {
    let schema = Joi.string().uuid();

    let result = Util.randomUuid();

    Joi.assert(result, schema);
});

test(`random uuid no dash`, async () => {
    let schema = Joi.string().uuid();

    let result = Util.randomUuid({ no_dash: true });

    Joi.assert(result, schema);
    expect(result).toEqual(expect.not.stringContaining('-'));
});
